mapper.select:
SELECT
card.id as id,goodsNumber,cardNumber,userId,errorStatus,activeDate,account,provinceId,cityId,countyId
FROM  zgk_card card
left join zgk_user_account account on card.userId=account.id
left join zgk_user_info info on card.userId=info.id



        and card.id in (${condition.whereSql})


        AND (account.account=#{condition.queryParam} OR  card.cardNumber=#{condition.queryParam})


        AND (info.provinceId=#{condition.provinceId}


        AND (info.cityId=#{condition.cityId}


        AND (info.countyId=#{condition.countyId}


        AND (card.activeDate > #{condition.activeDateStart} AND card.activeDate <  #{condition.activeDateEnd})


        AND card.errorStatus=#{condition.errorStatus}



    ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}

SELECT
count(card.id)
FROM  zgk_card card
left join zgk_user_account account on card.userId=account.id
left join zgk_user_info info on card.id=info.id


        and card.id in (${whereSql})


        AND (account.account=#{queryParam} OR  card.cardNumber=#{queryParam})


        AND (info.provinceId=#{provinceId}


        AND (info.cityId=#{cityId}


        AND (info.countyId=#{countyId}


        AND (card.activeDate > #{activeDateStart} AND card.activeDate <  #{activeDateEnd})


        AND card.errorStatus=#{errorStatus}
