mapper.select:
SELECT account.id AS id, goodsNumber, cardNumber, userId, errorStatus, activeDate, account, info.provinceId, cityId, countyId FROM zgk_user_account account INNER JOIN zgk_user_info info ON account.id = info.id LEFT JOIN zgk_card card ON card.userId = account.id
and info.id in (${condition.whereSql})
AND (account.account=#{condition.queryParam} OR card.cardNumber=#{condition.queryParam})
AND info.provinceId like #{condition.provinceId}
AND info.cityId like #{condition.cityId}
AND info.countyId like #{condition.countyId}
AND (card.activeDate > #{condition.activeDateStart} AND card.activeDate < #{condition.activeDateEnd})
AND card.errorStatus=#{condition.errorStatus}
AND (card.errorStatus=#{condition.errorStatus2} OR card.errorStatus IS NULL)
and card.status = #{condition.status}
and (card.status IS NULL or card.status = #{condition.status2})
ORDER BY ${orderBy} ${sortBy}
limit ${offset}, ${rows}

SELECT
count(account.id) from
