mapper.select:
SELECT

FROM ms_class_notice_statistics

    AND id = #{id}


ORDER BY ${orderBy} ${sortBy}

SELECT

FROM ms_class_notice_statistics WHERE
${property} = #{value}

    ORDER BY ${orderBy} ${sortBy}

select sum(num) as num,dateDay from ms_class_notice_statistics where dateDay BETWEEN #{condition.dateStart} and #{condition.dateEnd}

    and  areaId in
    #{item}





    and  schoolId in
    #{item}





GROUP BY dateDay ORDER BY dateDay ASC

select areaId,areaName,SUM(NUM) AS NUM FROM ms_class_notice_statistics WHERE dateDay =#{condition.dateDay}

    and  areaId in
    #{item}






    and  schoolId in
    #{item}




group by areaId,areaName

    ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}

select schoolId,schoolName,SUM(NUM) AS NUM FROM ms_class_notice_statistics WHERE dateDay =#{condition.dateDay} and areaId=#{condition.areaId}

    and  schoolId in
    #{item}


group by schoolId,schoolName

    ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}

select classId,className,SUM(NUM) AS NUM FROM ms_class_notice_statistics WHERE dateDay =#{condition.dateDay} and schoolId=#{condition.schoolId}  group by classId,className

    ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}

select count(*) from ( select areaId,areaName,SUM(NUM) AS NUM FROM ms_class_notice_statistics WHERE dateDay =#{condition.dateDay}

    and  areaId in
    #{item}





    and  schoolId in
    #{item}






group by areaId,areaName ) as c

select count(*) from ( select schoolId,schoolName,SUM(NUM) AS NUM FROM ms_class_notice_statistics WHERE dateDay =#{condition.dateDay} and areaId=#{condition.areaId}

    and  schoolId in
    #{item}


group by schoolId,schoolName) as c

SELECT

FROM ms_class_notice_statistics


        AND id = #{condition.id}


        AND classId = #{condition.classId}


        AND className = #{condition.className}


        AND schoolId = #{condition.schoolId}


        AND schoolName = #{condition.schoolName}


        AND areaId = #{condition.areaId}


        AND areaName = #{condition.areaName}


        AND dateDay = #{condition.dateDay}


        AND num = #{condition.num}



    ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}

SELECT count(id) FROM ms_class_notice_statistics


        AND id = #{condition.id}


        AND classId = #{condition.classId}


        AND className = #{condition.className}


        AND schoolId = #{condition.schoolId}


        AND schoolName = #{condition.schoolName}


        AND areaId = #{condition.areaId}


        AND areaName = #{condition.areaName}


        AND dateDay = #{condition.dateDay}


        AND num = #{condition.num}
