mapper.select:
select sum(teacherNum) as teacherNum,sum(parentNum) as parentNum,date_format(t.dateDay,'%Y-%m-%d') as dateDay
from
( select createDate as dateDay,count(1) as teacherNum,0 as parentNum,areaId
from ms_teacher_info_statistics
where areaId is not NULL and createDate BETWEEN #{condition.dateStart} and #{condition.dateEnd}

    and areaId in
    #{item}



    and schoolId in
    #{item}


group by date_format(createDate,'%Y-%m-%d'),areaId
UNION ALL
select createDate as dateDay,0 as teacherNum,count(1) as parentNum,areaId
from ms_parent_info_statistics
where areaId is not NULL and createDate BETWEEN #{condition.dateStart} and #{condition.dateEnd}

    and areaId in
    #{item}



    and schoolId in
    #{item}


group by date_format(createDate,'%Y-%m-%d'),areaId ) t
group by date_format(t.dateDay,'%Y-%m-%d')

SELECT count(1) FROM (SELECT i.id as areaId,mc as areaName,if (isnull (a.teacherNum),0,a.teacherNum) as teacherNum,if (isnull(b.parentNum),0,b.parentNum) as parentNum FROM microschool.xzq i LEFT JOIN (select areaName,areaId,count(1) as teacherNum from ms_teacher_info_statistics where createDate LIKE "%"#{condition.dateDay}"%"
and schoolId in #{item}
group by areaId) a on i.id=a.areaId LEFT JOIN (select areaName,areaId,count(1) as parentNum from ms_parent_info_statistics where createDate LIKE "%"#{condition.dateDay}"%"
and schoolId in #{item}
group by areaId) b on i.id = b.areaId WHERE 1=1 and i.mc in ('灞桥分部','碑林分部','沣东分部','高陵分公司','航天分公司','户县分公司','蓝田分公司','莲湖分部','临潼分公司','未央分部','新城分部','阎良分公司','雁塔分部','杨凌分公司','长安分公司','周至分公司','测试分部')
and i.id in #{item}
) t

SELECT i.id as areaId,mc as areaName,if (isnull (a.teacherNum),0,a.teacherNum) as teacherNum,if (isnull(b.parentNum),0,b.parentNum) as parentNum FROM microschool.xzq i LEFT JOIN (select areaName,areaId,count(1) as teacherNum from ms_teacher_info_statistics where createDate LIKE "%"#{condition.dateDay}"%"
and schoolId in #{item}
group by areaId) a on i.id=a.areaId LEFT JOIN (select areaName,areaId,count(1) as parentNum from ms_parent_info_statistics where createDate LIKE "%"#{condition.dateDay}"%"
and schoolId in #{item}
group by areaId) b on i.id = b.areaId WHERE 1=1 and i.mc in ('灞桥分部','碑林分部','沣东分部','高陵分公司','航天分公司','户县分公司','蓝田分公司','莲湖分部','临潼分公司','未央分部','新城分部','阎良分公司','雁塔分部','杨凌分公司','长安分公司','周至分公司','测试分部')
and i.id in #{item}
limit ${offset}, ${rows}

SELECT count(1) FROM ( SELECT x.school_id as schoolId,x.schoolName as schoolName,if (isnull (a.teacherNum),0,a.teacherNum) as teacherNum,if (isnull(b.parentNum),0,b.parentNum) as parentNum FROM microschool.v_parent_student_class x LEFT JOIN (select schoolName,schoolId,count(1) as teacherNum from ms_teacher_info_statistics where createDate LIKE "%"#{condition.dateDay}"%" group by schoolId) a on x.school_id =a.schoolId LEFT JOIN (select schoolName,schoolId,count(1) as parentNum from ms_parent_info_statistics where createDate LIKE "%"#{condition.dateDay}"%" group by schoolId) b on x.school_id = b.schoolId WHERE xzq_id = #{condition.areaId}
and x.school_id in #{item}
group by x.school_id ) t

SELECT x.school_id as schoolId,x.schoolName as schoolName,if (isnull (a.teacherNum),0,a.teacherNum) as teacherNum,if (isnull(b.parentNum),0,b.parentNum) as parentNum FROM microschool.v_parent_student_class x LEFT JOIN (select schoolName,schoolId,count(1) as teacherNum from ms_teacher_info_statistics where createDate LIKE "%"#{condition.dateDay}"%" group by schoolId) a on x.school_id =a.schoolId LEFT JOIN (select schoolName,schoolId,count(1) as parentNum from ms_parent_info_statistics where createDate LIKE "%"#{condition.dateDay}"%" group by schoolId) b on x.school_id = b.schoolId WHERE xzq_id = #{condition.areaId}
and x.school_id in #{item}
group by x.school_id
limit ${offset}, ${rows}

SELECT count(1) FROM ( SELECT n.class_id, nickName as className,if (isnull (a.teacherNum),0,a.teacherNum) as teacherNum,if (isnull(b.parentNum),0,b.parentNum) as parentNum FROM microschool.v_parent_student_class n LEFT JOIN (select className,classId,count(1) as teacherNum from ms_teacher_info_statistics where createDate LIKE "%"#{condition.dateDay}"%" group by classId) a on n.class_id = a.classId LEFT JOIN (select className,classId,count(1) as parentNum from ms_parent_info_statistics where createDate LIKE "%"#{condition.dateDay}"%" group by classId) b on n.class_id = b.classId WHERE school_id = #{condition.schoolId}
and n.class_id in #{item}
group by n.class_id ) t

SELECT n.class_id as classId, nickName as className,if (isnull (a.teacherNum),0,a.teacherNum) as teacherNum,if (isnull(b.parentNum),0,b.parentNum) as parentNum FROM microschool.v_parent_student_class n LEFT JOIN (select className,classId,count(1) as teacherNum from ms_teacher_info_statistics where createDate LIKE "%"#{condition.dateDay}"%" group by classId) a on n.class_id = a.classId LEFT JOIN (select className,classId,count(1) as parentNum from ms_parent_info_statistics where createDate LIKE "%"#{condition.dateDay}"%" group by classId) b on n.class_id = b.classId WHERE school_id = #{condition.schoolId}
and n.class_id in #{item}
group by n.class_id
limit ${offset}, ${rows}

SELECT parentName,parentId,loginNumber,areaName,schoolName,className,childName,studentId
FROM ms_parent_info_statistics
WHERE createDate LIKE "%"#{condition.dateDay}"%" AND classId = #{condition.classId}

    limit ${offset}, ${rows}

SELECT createDate,teacherName,teacherId,loginNumber,areaName,schoolName,className
FROM ms_teacher_info_statistics
WHERE createDate LIKE "%"#{condition.dateDay}"%" AND classId = #{condition.classId}

    limit ${offset}, ${rows}
