mapper.select:
select t0.order_no as orderNo , t0.channle as channle , t0.create_date as createDate , t0.goods_address as registAddress , t0.goods_count as goodsCount , t0.handle_state as handleState , t1.name as userName , t2.account as phoneNum from zgk_order t0 , zgk_user_info t1 , zgk_user_account t2 where t0.user_id = t1.id and t1.id = t2.id
and t0.channle = #{orderFrom}
and t0.handle_state = #{handleState}
and t0.department_code = #{departmentCode}
and (t0.order_no = #{orderNoOrPhone} or t2.account = #{orderNoOrPhone})
and t0.`status` = 1 order by create_date desc LIMIT #{index},#{pageSize}

select count(*) from zgk_order t0 , zgk_user_info t1 , zgk_user_account t2 where t0.user_id = t1.id and t1.id = t2.id
and t0.channle = #{orderFrom}
and t0.handle_state = #{handleState}
and t0.department_code = #{departmentCode}
and (t0.order_no = #{orderNoOrPhone} or t2.account = #{orderNoOrPhone})
and t0.status = 1

select sum(goods_count) from zgk_order where status = 1
and department_code = #{departmentCode}
and handle_state = 0

select t1.name as userName , t1.id as userId , t0.user_phone as phoneNum , t0.user_phone as account , count( case when t0.reward_level = 1 then t0.user_id end ) as firstLevelCount , count( case when t0.reward_level = 2 then t0.user_id end ) as secondLevelCount , sum(t0.price) as allIncome from zgk_split_price t0 , zgk_user_info t1 where t0.user_id = t1.id
and t1.provinceId = #{areaCode}
and t1.cityId = #{areaCode}
and t1.countyId = #{areaCode}
and t0.user_phone = #{account}
and type = 1 GROUP BY t0.user_id limit #{index},#{pageSize}

select
    count(DISTINCT t0.user_id)
from
    zgk_split_price t0 ,
    zgk_user_info t1
where
    t0.user_id = t1.id

        and t1.provinceId = #{areaCode}


        and t1.cityId = #{areaCode}


        and t1.countyId = #{areaCode}


        and t0.user_phone = #{account}

    and type = 1
